// Webhook에서 받은 여러 파일을 개별 아이템으로 분리
const items = [];

// Binary data가 배열인지 확인
const inputItem = $input.first();

if (inputItem.binary) {
  // binary 객체의 모든 키를 순회
  const binaryKeys = Object.keys(inputItem.binary);

  console.log(`총 ${binaryKeys.length}개 파일 감지`);

  for (const key of binaryKeys) {
    items.push({
      json: {},
      binary: {
        data: inputItem.binary[key]
      }
    });
  }
}

console.log(`${items.length}개 아이템으로 분리 완료`);

return items;

// OpenAI 응답 파싱
const response = $input.first().json;
const prepareData = $node['이미지 준비1'].json;

// 에러 체크
if (response.error) {
  console.error('OpenAI API 에러:', response.error);
  return [{
    json: {
      student_id: prepareData.batch_index,
      filename: prepareData.filename,
      error: true,
      error_message: response.error.message,
      extracted_text: ""
    }
  }];
}

// 정상 응답 처리
const extractedText = response.choices?.[0]?.message?.content || "";

console.log(`[${prepareData.batch_index}] 추출 완료: ${extractedText.length}자`);

return [{
  json: {
    student_id: prepareData.batch_index,
    filename: prepareData.filename,
    extracted_text: extractedText,
    char_count: extractedText.length,
    word_count: extractedText.split(/\s+/).filter(w => w).length,
    extracted_at: new Date().toISOString(),
    model: response.model || 'gpt-4o'
  }
}];

// Aggregate로 수집된 모든 결과 포맷팅
const allResults = $input.all();

const students = allResults.map(item => ({
  student_id: item.json.student_id,
  filename: item.json.filename,
  answer: item.json.extracted_text,
  metadata: {
    char_count: item.json.char_count,
    word_count: item.json.word_count,
    extracted_at: item.json.extracted_at,
    model: item.json.model
  },
  status: item.json.error ? 'failed' : 'success',
  error: item.json.error_message || null
}));

return [{
  json: {
    total_students: students.length,
    processed_at: new Date().toISOString(),
    students: students
  }
}];

// 현재 배치의 이미지를 base64로 준비
const item = $input.first();

// Binary data 확인
if (!item.binary || !item.binary.data) {
  return [{
    json: {
      error: '이미지 데이터가 없습니다.',
      index: $node['Split In Batches'].context['currentBatch']
    }
  }];
}

const binaryData = item.binary.data;

// n8n의 getBinaryDataBuffer helper 사용
const binaryDataBuffer = await this.helpers.getBinaryDataBuffer(0, 'data');
const imageBase64 = binaryDataBuffer.toString('base64');

const mimeType = binaryData.mimeType || 'image/jpeg';
const fileName = binaryData.fileName || 'image.jpg';
const batchIndex = $node['Split In Batches'].context['currentBatch'] + 1;

console.log(`[${batchIndex}] 이미지 base64 변환:`, {
  fileName,
  mimeType,
  base64Length: imageBase64.length,
  sizeKB: Math.round(imageBase64.length / 1024)
});

return [{
  json: {
    image_base64: imageBase64,
    mime_type: mimeType,
    filename: fileName,
    batch_index: batchIndex
  }
}];

