// 읽어온 이미지를 base64로 변환
const binaryData = $input.first().binary?.data;

if (!binaryData) {
  throw new Error("이미지 파일을 읽을 수 없습니다.");
}

// base64 데이터 추출
let imageBase64;
if (typeof binaryData.data === 'string') {
  imageBase64 = binaryData.data;
} else if (Buffer.isBuffer(binaryData.data)) {
  imageBase64 = binaryData.data.toString('base64');
} else {
  imageBase64 = binaryData.data;
}

const mimeType = binaryData.mimeType || 'image/jpeg';
const fileName = binaryData.fileName || 'image.jpg';

console.log(`이미지 로드 완료: ${fileName} (${mimeType})`);

return [{
  json: {
    image_base64: imageBase64,
    mime_type: mimeType,
    filename: fileName
  }
}];

