// AI Agent 응답 파싱
const aiResponse = $input.item.json.output;
let gradingResult;

try {
  // JSON 코드 블록에서 실제 JSON 추출
  const jsonMatch = aiResponse.match(/```json\s*([\s\S]*?)\s*```/) || aiResponse.match(/\{[\s\S]*\}/);
  const jsonString = jsonMatch ? (jsonMatch[1] || jsonMatch[0]) : aiResponse;
  gradingResult = JSON.parse(jsonString);
} catch (error) {
  gradingResult = {
    error: "JSON 파싱 실패",
    raw_response: aiResponse
  };
}

// 학생 답안도 함께 반환
gradingResult.student_answer = $('프롬프트 준비').item.json.student_answer;

return [{
  json: gradingResult
}];
